import { useContext } from "react";
import { TokenContext } from "../context/TokenContext";
import { UserContext } from "../context/UserContext";
import {NavLink} from "react-router-dom";
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';


export default function NavBar() {
    const {setToken, removeToken} = useContext(TokenContext);
    const { currentUser, setCurrentUser, removeUser } = useContext(UserContext);

    const handleLogout = () => {
        removeToken();
        removeUser();
        setCurrentUser('');
        setToken('')
    }

    return (
        <Navbar bg="light">

                <Navbar.Brand>
                    <NavLink to='/' >Jobly</NavLink>
                </Navbar.Brand>
                {currentUser ? 
                <>
                    <Nav className="justify-content-end">
                        <Nav.Item>
                            <Nav.Link>
                                <NavLink to='/companies'>Companies</NavLink>
                            </Nav.Link>
                        </Nav.Item>

                        <Nav.Item>
                            <Nav.Link>
                                <NavLink to='/jobs'>Jobs</NavLink>
                            </Nav.Link>
                        </Nav.Item>
                        <Nav.Item>
                            <Nav.Link>
                                <NavLink to='/profile'>Profile</NavLink>
                            </Nav.Link>
                        </Nav.Item>
                        <Nav.Item>
                            <Nav.Link>
                                <NavLink onClick={handleLogout} to='/'>Logout {currentUser}</NavLink>
                            </Nav.Link>    
                        </Nav.Item>   
                    </Nav>
                </>

                 :
                 <>
                    <Nav className="justify-content-end">
                        <Nav.Item>
                            <Nav.Link><NavLink to='/login'>Login</NavLink></Nav.Link>
                        </Nav.Item>
                        <Nav.Item>
                            <Nav.Link><NavLink to='/signup'>Signup</NavLink></Nav.Link>
                        </Nav.Item>
                    </Nav>

                 </>
                 }  

        </Navbar>
    )
}
