import useToggle from "../hooks/useToggle"

export default function JobCardButton({jobId}) {
    const [hasApplied, toggle ] = useToggle(false)

    return (
        <button onClick={toggle}>
            {hasApplied? "Applied" : "Apply"}
        </button>
    )
}
