import { useContext } from "react";
import { TokenContext } from "../context/TokenContext";
import { UserContext } from "../context/UserContext";
import {NavLink} from "react-router-dom";
import Container from "react-bootstrap/Container";
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';


export default function NavBar() {
    const {setToken, removeToken} = useContext(TokenContext);
    const { currentUser, setCurrentUser, removeUser } = useContext(UserContext);

    const handleLogout = () => {
        removeToken();
        removeUser();
        setCurrentUser('');
        setToken('')
    }

    return (
        <Navbar bg="light">
            <Container>
            <ul>
                <Navbar.Brand>
                    <NavLink to='/' >Jobly</NavLink>
                </Navbar.Brand>
                {currentUser ? 
                <>
                    <Nav.Link>
                        <NavLink to='/companies'>Companies</NavLink>
                    </Nav.Link>
                    <Nav.Link>
                        <NavLink to='/jobs'>Jobs</NavLink>
                    </Nav.Link>
                    <Nav.Link>
                        <NavLink to='/profile'>Profile</NavLink>
                    </Nav.Link>
                    <Nav.Link>
                        <NavLink onClick={handleLogout} to='/'>Logout {currentUser}</NavLink>
                    </Nav.Link>
                </>

                 :
                 <>
                    <Nav.Link><NavLink to='/login'>Login</NavLink></Nav.Link>
                    <Nav.Link><NavLink to='/signup'>Signup</NavLink></Nav.Link>
                 </>
                 }  

            </ul>
            </Container>
        </Navbar>
    )
}
