import { useState, useContext } from 'react';
import { TokenContext } from '../context/TokenContext';
import { UserContext } from '../context/UserContext';
import { FormDataContext } from '../context/FormDataContext';
import JoblyApi from '../api';
import Button from './Button';

export default function SignupForm() {
    // const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    // const [fname, setFname] = useState('');
    // const [lname, setLname] = useState('');
    // const [email, setEmail] = useState('');
    const {username, setUsername, fname, setFname, lname, setLname, email, setEmail} = useContext(FormDataContext);
    const {setToken} = useContext(TokenContext);
    const {setCurrentUser} = useContext(UserContext);

    function handleChange(evt) {
        switch (evt.target.name) {
            case 'password':
                setPassword(evt.target.value);
                break;
            case 'username':
                setUsername(evt.target.value);
                break;
            case 'fname':
                setFname(evt.target.value);
                break;
            case 'lname':
                setLname(evt.target.value);
                break;
            case 'email':
                setEmail(evt.target.value);
                break;
            default:
                break;
        }
     }

     async function handleSubmit(evt) {
        try {
            const token = await JoblyApi.signupUser(username, password, fname, lname, email)
            setToken(token);
            setCurrentUser(username);  
        } catch (err) {
            console.error(err);
        }
     }

    return (
        <>
           <form>
            <label htmlFor="username">Username</label>
            <input 
                type="text" 
                name="username" 
                id="username" 
                autoComplete='username' 
                onChange={handleChange} 
                value={username} 
            />
            <label htmlFor="password">Password</label>
            <input 
                type="password" 
                name="password" 
                id="password" 
                autoComplete='current-password' 
                onChange={handleChange} 
                value={password}
            />
            <label htmlFor="fname">First Name</label>
            <input
                type="text"
                name="fname"
                id="fname"
                autoComplete='fname'
                onChange={handleChange}
                value={fname}
            />
            <label htmlFor="lname">Last Name</label>
            <input
                type="text"
                name="lname"
                id="lname"
                autoComplete='lname'
                onChange={handleChange}
                value={lname}
            />
            <label htmlFor="email">Email</label>
            <input
                type="email"
                name="email"
                id="email"
                autoComplete='email'
                onChange={handleChange}
                value={email}
            />

            <Button path='/' text='Sign Up' func={handleSubmit} />

           </form>
        </>
    )
}
