import {Link} from "react-router-dom";
import './CompanyCard.css';
import Paper from '@mui/material/Paper'
import Typography from '@mui/material/Typography'

export default function CompanyCard({companies}) {

    return (
        <>
            {companies.map(c => (
                <Paper elevation={5} sx={{my: 3, p: 3}} >
                    <Link className="CompanyCard" key={crypto.randomUUID()} to={`/companies/${c.handle}`}>
                        <div>
                            <Typography variant="h4" component="h2" sx={{marginBottom: 2}}>{c.name}</Typography>
                            <Typography variant="body2" component="p">{c.description}</Typography>
                        </div>
                    </Link>
                </Paper>

            ))}
        </>
    )
}
