import {useState, useContext, useHistory} from 'react';
import { SearchContext } from '../context/SearchContext';
import useDebounce from '../hooks/useDebounce';
import JoblyApi from '../api';

export default function SearchBar() {
    const [term, setTerm] = useState('');
    const {companies, setCompanies} = useContext(SearchContext);
    const history = useHistory();

    // useDebounce(() => {
    //     const res = JoblyApi.filterCompanies(term)
    //     setCompanies(res);
    // }, 1000, [term])

    function handleChange(evt){
        setTerm(evt.target.value);
    }

    function handleSubmit(evt){
        evt.preventDefault();
        const res = JoblyApi.filterCompanies(term)
        setCompanies(res)
        history.push(`/search?q=${term}`)
    }

    return (
        <>
            <form onSubmit={handleSubmit}>
                <input type="text" placeholder="Search" onChange={handleChange} value={term} />
                <button type='submit '>Submit</button>
            </form>
        </>
    )
}
